import React, { useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom"; // Use navigate and location from React Router

const Home = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const navigate = useNavigate();
  const location = useLocation(); // Track current location

  const handleSearch = (e) => {
    e.preventDefault();
    // Handle search logic here (e.g., filtering subjects)
    console.log("Search query:", searchQuery);
  };

  // Function to render content based on the current page (URL path)
  const renderContent = () => {
    switch (location.pathname) {
      case "/subject-view-page":
        return <SubjectViewPage />;
      case "/resource-search-page":
        return <ResourceSearchPage />;
      case "/file-upload-page":
        return <FileUploadPage />;
      case "/oer-page":
        return <OERPage />;
      case "/contributors-page":
        return <ContributorsPage />;
      case "/self-page":
        return <SelfDirectedLearningPage />;
      case "/register-page":
        return <RegisterPage />;
      case "/reset-password-page":
        return <ResetPasswordPage />;
      case "/about-us-page":
        return <AboutUsPage />;
      case "/faq-page":
        return <FAQPage />;
      default:
        return <Overview />;
    }
  };

  return (
    <div style={styles.pageContainer}>
      {/* Sidebar */}
      <aside style={styles.sidebar}>
        <nav>
          <ul style={styles.sidebarList}>
            <li style={styles.sidebarListItem}>
              <Link to="/subject-view-page" style={styles.link}>
                Subject View
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/resource-search-page" style={styles.link}>
                Search Results
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/file-upload-page" style={styles.link}>
                Contribute
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/oer-page" style={styles.link}>
                Other Useful OERs
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/contributors-page" style={styles.link}>
                Contributors
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/self-page" style={styles.link}>
                Self-Directed Learning
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/register-page" style={styles.link}>
                Account Creation
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/reset-password-page" style={styles.link}>
                Password Reset
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/about-us-page" style={styles.link}>
                About Us
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/faq-page" style={styles.link}>
                FAQ
              </Link>
            </li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main style={styles.mainContent}>
        <header style={styles.header}>
          <h1>Share2Teach</h1>
        </header>
        <section style={styles.searchSection}>
          <form onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search subjects..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              style={styles.searchInput}
            />
            <button type="submit" style={styles.searchButton}>
              Search
            </button>
          </form>
        </section>

        {/* Dynamic Content */}
        <section style={styles.banner}>
          <div style={styles.bannerText}>{renderContent()}</div>
        </section>
      </main>
    </div>
  );
};

// Example pages for dynamic content
const Overview = () => (
  <div>
    <h2>Overview</h2>
    <p>Share2Teach is an open educational resource project...</p>
    {/* ... more content ... */}
  </div>
);

const SubjectViewPage = () => (
  <div>
    <h2>Subject View</h2>
    <p>Display subject information here...</p>
  </div>
);

const ResourceSearchPage = () => (
  <div>
    <h2>Search Results</h2>
    <p>Show search results here...</p>
  </div>
);

// Similarly, create other page components: FileUploadPage, OERPage, ContributorsPage, etc.

// Styles for the page
const styles = {
  pageContainer: {
    display: "flex",
    height: "100vh",
  },
  sidebar: {
    width: "200px",
    backgroundColor: "#2c2c2c",
    padding: "10px",
    color: "white",
  },
  sidebarList: {
    listStyleType: "none",
    padding: 0,
  },
  sidebarListItem: {
    marginBottom: "8px",
  },
  link: {
    color: "white",
    textDecoration: "none",
    padding: "8px 12px",
    borderRadius: "4px",
    display: "block",
    transition: "background-color 0.3s, transform 0.3s",
  },
  linkHover: {
    backgroundColor: "#4CAF50",
    transform: "scale(1.05)",
  },
  mainContent: {
    flex: 1,
    padding: "20px",
    backgroundColor: "#f4f4f4",
    overflowY: "auto",
  },
  header: {
    textAlign: "center",
    paddingBottom: "20px",
  },
  searchSection: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "20px",
  },
  searchInput: {
    padding: "10px",
    width: "250px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginRight: "10px",
  },
  searchButton: {
    padding: "10px 15px",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  banner: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#e4e4e4",
    padding: "20px",
    borderRadius: "8px",
    marginBottom: "30px",
  },
  bannerText: {
    textAlign: "left",
  },
};

export default Home;
