import React from "react";
import { Link } from "react-router-dom"; // Ensure you have react-router-dom installed

const SubjectView = () => {
  return (
    <div style={styles.pageContainer}>
      <aside style={styles.sidebar}>
        <nav>
          <ul style={styles.sidebarList}>
            <li style={styles.sidebarListItem}>
              <Link to="/resource-search-page" style={styles.link}>
                Search Results
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/file-upload-page" style={styles.link}>
                Contribute
              </Link>
            </li>
            <li style={styles.sidebarListItem}>
              <Link to="/faq" style={styles.link}>
                FAQ
              </Link>
            </li>
          </ul>
        </nav>
      </aside>

      <main style={styles.mainContent}>
        <header style={styles.header}>
          <h1>Subject View</h1>
        </header>
        <section>
          <p>
            This is the subject view page where subjects will be displayed and
            managed.
          </p>
        </section>
      </main>
    </div>
  );
};

// Styles for the page
const styles = {
  pageContainer: {
    display: "flex",
    height: "100vh",
  },
  sidebar: {
    width: "200px",
    backgroundColor: "#2c2c2c",
    padding: "10px",
    color: "white",
  },
  sidebarList: {
    listStyleType: "none",
    padding: 0,
  },
  sidebarListItem: {
    marginBottom: "8px",
  },
  link: {
    color: "white",
    textDecoration: "none",
    padding: "8px 12px",
    borderRadius: "4px",
    display: "block",
    transition: "background-color 0.3s, transform 0.3s",
  },
  linkHover: {
    backgroundColor: "#4CAF50",
    transform: "scale(1.05)",
  },
  mainContent: {
    flex: 1,
    padding: "20px",
    backgroundColor: "#f4f4f4",
    overflowY: "auto",
  },
  header: {
    textAlign: "center",
    paddingBottom: "20px",
  },
};

export default SubjectView;
