import React, { useState } from "react";
import { Link } from "react-router-dom"; // Ensure react-router-dom is installed

const Self = () => {
  const [searchQuery, setSearchQuery] = useState("");

  // Sample resource data
  const resources = [
    { title: "Codecademy - Learn Python", description: "Free interactive Python course", link: "https://www.codecademy.com/learn/learn-python" },
    { title: "Khan Academy", description: "Courses on various subjects", link: "https://www.khanacademy.org" },
    { title: "Harvard's CS50", description: "Intro to Computer Science course", link: "https://www.edx.org/course/cs50s-introduction-to-computer-science" },
    { title: "FreeCodeCamp", description: "Free web development tutorials", link: "https://www.freecodecamp.org" },
  ];

  const [filteredResources, setFilteredResources] = useState(resources);

  // Handle search functionality
  /*const handleSearch = (e) => {
    e.preventDefault();
    const filtered = resources.filter(resource =>
      resource.title.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredResources(filtered);
  };*/
  const handleSearch = (e) => {
    e.preventDefault();
    console.log("Search query:", searchQuery);
  };

  return (
    <div style={styles.pageContainer}>
      <main style={styles.mainContent}>
        <header style={styles.header}>
          <h1>Self-Directed Learning Resources</h1>
        </header>

        {/* Search Section */}
        <section style={styles.searchSection}>
        <form onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search contributors..."
              style={styles.searchInput}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit" style={styles.searchButton}>Search</button>
          </form>
        </section>

        {/* Resources Section */}
        <section style={styles.resourcesSection}>
          {filteredResources.map((resource, index) => (
            <div key={index} style={styles.resourceCard}>
              <h2>{resource.title}</h2>
              <p>{resource.description}</p>
              <a href={resource.link} target="_blank" rel="noopener noreferrer" style={styles.link}>
                Access Resource
              </a>
            </div>
          ))}
        </section>
      </main>
    </div>
  );
};

// Styles for the page
const styles = {
  pageContainer: {
    display: "flex",
    height: "100vh",
  },
  mainContent: {
    flex: 1,
    padding: "20px",
    backgroundColor: "#f4f4f4",
    overflowY: "auto",
  },
  header: {
    textAlign: "center",
    paddingBottom: "20px",
  },
  searchSection: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "20px",
  },
  searchInput: {
    padding: "10px",
    width: "250px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginRight: "10px",
  },
  searchButton: {
    padding: "10px 15px",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  resourcesSection: {
    display: "grid",
    gridTemplateColumns: "repeat(3, 1fr)",
    gap: "20px",
  },
  resourceCard: {
    backgroundColor: "#e4e4e4",
    padding: "20px",
    borderRadius: "8px",
    textAlign: "center",
  },
  link: {
    color: "#4CAF50",
    textDecoration: "none",
    fontWeight: "bold",
  },
};

export default Self;
