import React, { useState, useEffect } from "react";

const ResourceReport = () => {
  const [resourceId, setResourceId] = useState(""); // For user-typed resource ID
  const [userId, setUserId] = useState("");
  const [reportComplaint, setReportComplaint] = useState("");
  const [resources, setResources] = useState([]); // State to hold the fetched resources
  const [filteredResources, setFilteredResources] = useState([]); // For filtering based on input

  // Fetch resource IDs from the API when the component mounts
  useEffect(() => {
    const fetchResources = async () => {
      try {
        const response = await fetch(
          "http://127.0.0.1:8000/api/resource/deserial"
        ); // Correct API endpoint
        if (!response.ok) throw new Error("Failed to fetch resources");
        const data = await response.json();
        setResources(data); // Set fetched resources
        setFilteredResources(data); // Initialize filtered resources
      } catch (error) {
        console.error("Error fetching resources:", error);
      }
    };

    fetchResources();
  }, []);

  // Handle input changes
  const handleResourceIdChange = (e) => {
    const value = e.target.value;
    setResourceId(value);

    // Filter resources to match typed input
    const filtered = resources.filter((resource) =>
      resource.id.toString().includes(value)
    );
    setFilteredResources(filtered);
  };
  const handleUserIdChange = (e) => setUserId(e.target.value);
  const handleReportComplaintChange = (e) => setReportComplaint(e.target.value);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const reportData = {
      resource_id: resourceId,
      user_id: userId,
      complaint: reportComplaint,
    };

    try {
      const response = await fetch(
        "http://127.0.0.1:8000/api/report/deserial",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(reportData), // Sending the data as JSON
        }
      );

      if (response.ok) {
        alert("Report submitted successfully!");
        setResourceId(""); // Clear form fields after successful submission
        setUserId("");
        setReportComplaint("");
      } else {
        const errorMessage = await response.json();
        alert("Failed to submit report. Please try again. " + errorMessage);
      }
    } catch (error) {
      console.error("Error submitting report:", error);
      alert("An error occurred while submitting the report.");
    }
  };

  return (
    <div style={styles.pageContainer}>
      <header style={styles.header}>
        <h1>Resource Report Form</h1>
      </header>

      {/* Resource Report Form */}
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label htmlFor="resourceId" style={styles.label}>
            Resource ID:
          </label>
          <input
            type="number"
            id="resourceId"
            name="resourceId"
            value={resourceId}
            onChange={handleResourceIdChange}
            style={styles.input}
            placeholder="Enter or select Resource ID"
            list="resource-list" // Attach datalist for suggestions
          />
          {/* Datalist for resource suggestions */}
          <datalist id="resource-list">
            {filteredResources.map((resource) => (
              <option key={resource.id} value={resource.id}>
                {resource.id}
              </option>
            ))}
          </datalist>
        </div>

        <div style={styles.formGroup}>
          <label htmlFor="userId" style={styles.label}>
            Report User:
          </label>
          <input
            type="text"
            id="userId"
            name="userId"
            value={userId}
            onChange={handleUserIdChange}
            style={styles.input}
            placeholder="Enter User ID"
          />
        </div>

        <div style={styles.formGroup}>
          <label htmlFor="reportComplaint" style={styles.label}>
            Complaint:
          </label>
          <textarea
            id="reportComplaint"
            name="reportComplaint"
            value={reportComplaint}
            onChange={handleReportComplaintChange}
            style={styles.textarea}
            placeholder="Enter your complaint"
          />
        </div>

        <div style={styles.submitButtonWrapper}>
          <button type="submit" style={styles.submitButton}>
            Submit Report
          </button>
        </div>
      </form>
    </div>
  );
};

// Styles for the page and form
const styles = {
  pageContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    minHeight: "100vh",
    backgroundColor: "#f9f9f9",
    padding: "20px",
  },
  header: {
    textAlign: "center",
    marginBottom: "30px",
  },
  form: {
    width: "100%",
    maxWidth: "500px",
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  formGroup: {
    marginBottom: "20px",
  },
  label: {
    display: "block",
    marginBottom: "8px",
    fontWeight: "bold",
    fontSize: "14px",
  },
  input: {
    width: "100%",
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "4px",
    fontSize: "14px",
  },
  textarea: {
    width: "100%",
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "4px",
    fontSize: "14px",
    minHeight: "100px",
  },
  submitButtonWrapper: {
    textAlign: "center",
  },
  submitButton: {
    padding: "10px 20px",
    backgroundColor: "#4CAF50",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
    transition: "background-color 0.3s",
  },
};

export default ResourceReport;
