import React, { useState, useEffect } from "react";

const ResourceSearch = () => {
  const [keywords, setKeywords] = useState("");
  const [allResources, setAllResources] = useState([]); // Store all resources
  const [filteredResources, setFilteredResources] = useState([]); // Store filtered resources

  // Fetch resources from the API
  useEffect(() => {
    const fetchResources = async () => {
      try {
        const response = await fetch('https://127.0.0.1/api/resource/deserial'); // Update with your actual API endpoint
        if (!response.ok) throw new Error('Failed to fetch resources');
        const data = await response.json();
        setAllResources(data); // Set the fetched resources
        setFilteredResources(data); // Set the filtered resources to display initially
      } catch (error) {
        console.error('Error fetching resources:', error);
      }
    };

    fetchResources();
  }, []);

  // Handle input changes
  const handleKeywordsChange = (e) => setKeywords(e.target.value);

  // Handle form submission
  const handleSearch = (e) => {
    e.preventDefault();
    if (keywords.trim() === "") {
      setFilteredResources(allResources); // If keywords are empty, show all resources
    } else {
      const filtered = allResources.filter(resource =>
        resource.resource_name.toLowerCase().includes(keywords.toLowerCase())
      );
      setFilteredResources(filtered); // Update filtered resources based on search
    }
  };

  return (
    <div>
      <form onSubmit={handleSearch} style={styles.searchForm}>
        <label htmlFor="keywords" style={styles.label}>Keywords:</label>
        <input
          type="text"
          name="keywords"
          id="keywords"
          placeholder="Search for resources based on keywords"
          value={keywords}
          onChange={handleKeywordsChange}
          style={styles.searchInput}
        />
        <button type="submit" style={styles.searchButton}>Search</button>
      </form>

      <h2>Search Results:</h2>
      <ul>
        {filteredResources.length > 0 ? (
          filteredResources.map((resource) => (
            <li key={resource.id}>
              {resource.resource_name} - {resource.subject} - {resource.grade}
            </li>
          ))
        ) : (
          <li>No resources found.</li>
        )}
      </ul>
    </div>
  );
};

// Styles for the search section
const styles = {
  searchForm: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "20px",
  },
  label: {
    marginRight: "10px",
    alignSelf: "center",
  },
  searchInput: {
    padding: "10px",
    width: "250px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginRight: "10px",
  },
  searchButton: {
    padding: "10px 15px",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
};

export default ResourceSearch;
