import React, { useState, useEffect } from "react";

const ResourceSearch = () => {
  const [resources, setResources] = useState([]); // Store fetched resources

  // Fetch resources from the API
  useEffect(() => {
    const fetchResources = async () => {
      try {
        const response = await fetch('https://127.0.0.1/api/resource/deserial'); // Update with your actual API endpoint
        if (!response.ok) throw new Error('Failed to fetch resources');
        const data = await response.json();
        setResources(data); // Set the fetched resources
      } catch (error) {
        console.error('Error fetching resources:', error);
      }
    };

    fetchResources();
  }, []);

  return (
    <div>
      <h2>Resources:</h2>
      <ul>
        {resources.length > 0 ? (
          resources.map((resource) => (
            <li key={resource.id}>
              {resource.resource_name} - {resource.subject} - {resource.grade}
            </li>
          ))
        ) : (
          <li>No resources found.</li>
        )}
      </ul>
    </div>
  );
};

// Styles for the display section
const styles = {
  // Add any styles you need here
};

export default ResourceSearch;
