import React, { useState } from "react";

const Self = () => {
  const [searchQuery, setSearchQuery] = useState("");

  // Mock data for subjects or topics related to self-directed learning
  const subjects = [
    { title: "Time Management in Self-Directed Learning", content: "Learn how to manage your time effectively." },
    { title: "Goal Setting for Self-Directed Learners", content: "Discover strategies to set achievable learning goals." },
    { title: "Using Online Resources for Self-Study", content: "Explore various online resources to enhance your learning." },
    { title: "Peer Learning in a Self-Directed Context", content: "Understand how to collaborate with peers while learning independently." },
    { title: "Tracking Your Progress as a Self-Directed Learner", content: "Tips on how to monitor your learning progress." },
    // Add more topics as needed
  ];

  // Function to handle search logic
  const handleSearch = (e) => {
    e.preventDefault();
    console.log("Search query:", searchQuery);
  };

  // Filter subjects based on the search query
  const filteredSubjects = subjects.filter(subject =>
    subject.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div style={styles.pageContainer}>
      {/* Main Content */}
      <main style={styles.mainContent}>
        <header style={styles.header}>
          <h1>Self-Directed Learning</h1>
        </header>

        {/* Search Section */}
        <div style={styles.searchSection}>
          <form onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search topics..."
              style={styles.searchInput}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit" style={styles.searchButton}>
              Search
            </button>
          </form>
        </div>

        {/* Display filtered topics */}
        <div style={styles.subjectList}>
          {filteredSubjects.length > 0 ? (
            filteredSubjects.map((subject, index) => (
              <div key={index} style={styles.subjectItem}>
                <h3>{subject.title}</h3>
                <p>{subject.content}</p>
              </div>
            ))
          ) : (
            <p>No topics found.</p>
          )}
        </div>
      </main>
    </div>
  );
};

// Styles for the page
const styles = {
  pageContainer: {
    display: "flex",
    height: "100vh",
  },
  mainContent: {
    flex: 1,
    padding: "20px",
    backgroundColor: "#f4f4f4",
    overflowY: "auto",
  },
  header: {
    textAlign: "center",
    paddingBottom: "20px",
  },
  searchSection: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "20px",
  },
  searchInput: {
    padding: "10px",
    width: "250px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginRight: "10px",
  },
  searchButton: {
    padding: "10px 15px",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  subjectList: {
    marginTop: "20px",
  },
  subjectItem: {
    marginBottom: "10px",
    border: "1px solid #ccc",
    borderRadius: "4px",
    padding: "10px",
    backgroundColor: "#e4e4e4",
  },
};

export default Self;
